import { graphql } from "gatsby"
import ProjectsComponent from "../components/projects"

export default ProjectsComponent

export const query = graphql`
  query {
    allProject(sort: { fields: title, order: DESC }) {
      nodes {
        title
        slug
        status
        excerpt
        body
        authors {
          name
          slug
        }
      }
    }
  }
`
